// Initialize buffer if first chunk
if(!context.buffer) context.buffer = {};

// Store chunk
const chunk = msg.payload;
if(!context.buffer[chunk.id]) {
    context.buffer[chunk.id] = {
        parts: [],
        expected: 0,
        received: 0
    };
}

context.buffer[chunk.id].parts[chunk.seq] = chunk.data;
context.buffer[chunk.id].received++;

// Check if all chunks received
if(context.buffer[chunk.id].received === context.buffer[chunk.id].expected) {
    // Combine and output
    const fullImage = "data:image/jpeg;base64," + 
        context.buffer[chunk.id].parts.join('');

    delete context.buffer[chunk.id];

    return {
        payload: {
            deviceid: chunk.id,
            img: fullImage
        }
    };
}

