// Increment counter
msg.counter = msg.counter || 0;
msg.counter++;

// If we've reached max count, stop
if (msg.counter >= msg.maxCount) {
    node.status({fill:"green",shape:"dot",text:"Completed "+msg.counter+" captures"});
    return null;
}

// Otherwise prepare for next capture
msg.topic = "/capture/" + (msg.deviceId || "your_device_id_here");
return msg;

if (!msg.payload || msg.payload.length === 0) {
    node.error("No active devices", msg);
    return null;
}
flow.set("devices", msg.payload);
flow.set("currentIndex", 0);
flow.set("loopCount", 0);
return { ...msg, deviceId: msg.payload[0].device_id };

const devices = flow.get("devices");
let index = flow.get("currentIndex");
let loopCount = flow.get("loopCount");

if (index >= devices.length) {
    flow.set("devices", null);
    return null;  // All devices done
}

msg.deviceId = devices[index].device_id;

if (loopCount < 5) {
    flow.set("loopCount", loopCount + 1);
    return msg;  // Continue loop
} else {
    flow.set("currentIndex", index + 1);
    flow.set("loopCount", 0);
    if (index + 1 < devices.length) {
        msg.deviceId = devices[index + 1].device_id;
        return msg;  // Next device
    } else {
        return null;  // Done
    }
}

